table {
        border-collapse: collapse;
        width: 100%;
    }
    th, td {
        border: 1px solid #dddddd;
        text-align: left;
        padding: 8px;
    }
    th {
        background-color: #f2f2f2;
    }
#bgcolor{
    background-color:#f2f2f2;
}
.print-format td, .print-format th {
    padding: 5px !important;
}
#fontsize{
    font-size:10px;
    color:black;
}

.print-format {
    font-size: 9px!important;
}

